mapper.select:
SELECT I.ITEMID, I.PRODUCTID, LISTPRICE, UNITCOST, SUPPLIER AS supplierId, I.PRODUCTID AS "product.productId", NAME AS "product.name", DESCN AS "product.description", CATEGORY AS "product.categoryId", STATUS, ATTR1 AS attribute1, ATTR2 AS attribute2, ATTR3 AS attribute3, ATTR4 AS attribute4, ATTR5 AS attribute5, QTY AS quantity, I.ISAUCTION AS isAuction, I.DEPOSIT AS deposit,
I.USERNAME AS username2 , I.closingTime AS closingTime, I.timeStatus AS timeStatus
FROM ITEM I, INVENTORY V, PRODUCT P WHERE P.PRODUCTID = I.PRODUCTID AND I.ITEMID = V.ITEMID AND I.ITEMID = #{value}

SELECT QTY AS value
FROM INVENTORY
WHERE ITEMID = #{itemId}

mapper.update:
UPDATE INVENTORY 
SET QTY = QTY - #{increment}
WHERE ITEMID = #{itemId}

UPDATE ITEM
SET AUCTIONID = #{maxAuctionId}
WHERE ITEMID = #{itemId}

mapper.select:
SELECT ISAUCTION
FROM ITEM
WHERE ITEMID = #{itemId}
